import { Pipe, PipeTransform } from '@angular/core';
import { capitalize } from '../utilities/utilities';

@Pipe({
  name: 'capitalCase',
  standalone: true,
})
export class CapitalCasePipe implements PipeTransform {
  transform(s: string): any {
    return capitalize(s);
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { dashed } from '../utilities/utilities';

@Pipe({
  name: 'dashCase',
  standalone: true,
})
export class DashCasePipe implements PipeTransform {
  transform(s: string): any {
    return dashed(s);
  }
}

import { Injector, Pipe, PipeTransform, Type } from '@angular/core';

@Pipe({
  name: 'dynamicPipe',
  standalone: true,
})
export class DynamicPipe implements PipeTransform {
  constructor(private injector: Injector) {}

  transform(value: any, requiredPipe: Type<any>, pipeArgs: any[]): any {
    const injector = Injector.create({
      name: 'DynamicPipe',
      parent: this.injector,
      providers: [{ provide: requiredPipe }],
    });
    const pipe = injector.get(requiredPipe);
    return pipe.transform(value, ...(pipeArgs || []));
  }
}

import {
