        value: 250,
      },
      {
        year: '2021',
        value: 50,
      },
      {
        year: '2022',
        value: 60,
      },
    ];
  }
  transpose(): void {
    if (this.tableConfig$.value.columns) {
      this.tableConfig$.next({
        stickyHeaders: {
          row: true,
          column: true,
        },
        rows: {
          year: {
            sortable: true,
            header: false,
            class: 'text-end',
          },
          value: {
            class: 'text-end',
          },
          delta: {
            header: 'Delta %',
            templateRef: this.delta,
            class: 'text-end',
          },
          deltaIndex: {
            header: 'Since inception %',
            templateRef: this.deltaIndex,
            class: 'text-end',
          },
          deltaAbsolute: {
            header: 'Delta',
            templateRef: this.deltaAbsolute,
            class: 'text-end',
          },
          combined: {
            header: 'Value with change',
            templateRef: this.combined,
            class: 'text-end text-nowrap',
          },
        },
      });
    } else {
      this.tableConfig$.next({
        stickyHeaders: {
          row: true,
          column: true,
        },
        columns: {
          year: {
            sortable: true,
          },
          value: {
            class: 'text-end',
          },
          delta: {
            header: 'Delta %',
            templateRef: this.delta,
            class: 'text-end',
          },
          deltaIndex: {
            header: 'Since inception %',
            templateRef: this.deltaIndex,
            class: 'text-end',
          },
          deltaAbsolute: {
