import { TemplateRef } from '@angular/core';
import { TableRow } from './table-row.interface';
export interface TableColumn<R = TableRow> {
  header?: string | false;
  mobileHeader?: string | boolean;
  hidden?: boolean;
  sortable?: boolean;
  order?: number;
  search?: boolean | SearchFunction<R>;
  templateRef?: TemplateRef<any>;
  transform?: {
    pipe: any;
    args?: Array<any>;
  };
  class?: string;
  mapTo?: {
    path: string;
    missingValue?: string | number;
  };
}

export type SearchFunction<R = TableRow> = (
  row: R,
  column: keyof R,
  value: typeof row[keyof R]
) => string;

import { TableColumn } from './table-column.interface';
import { TableRow } from './table-row.interface';

export interface TableConfig<R = TableRow> {
  /** Disable sorting of table data, useful when sorting is handled externally e.g. server-side pagination. Table will still use sortOrder to visually show how the data is sorted. <p>**Default:** `false`</p> */
  disableTableSort?: boolean;
  /** Allows for a more mobile friendly layout by displaying columns as rows instead. This is accomplished with css by adding `table-mobile` class to table element and adding column headers as data labels to each row. <p>**Default:** `false`</p>*/
  mobileLayout?: boolean;
  /** Make row and/or column headers sticky by adding `gt-sticky-row-header` and `gt-sticky-column-header` to table to alter table behaviour using css. Please note that in order for headers to be sticky, table can't be placed inside element with overflow set to auto. <p>**Default:** `{row:false, column:false}`</p>*/
  stickyHeaders?: {
    row?: boolean;
    column?: boolean;
  };
  /** Add one or more CSS classes to the table element e.g. `table table-striped table-bordered`. <p>**Default:** `table`</p>*/

  class?: string;
  rows?: {
    [Property in keyof R]: TableColumn<R>;
  };
  columns?: {
    [Property in keyof R]: TableColumn<R>;
  };
  pagination?: {
    length?: number;
  };
  rowClick?: boolean;
  rowHover?: boolean;
  footer?: {
    headers?: {
      [key: FooterCalculation | string]: string | boolean;
    };
    columns?: {
      [Property in keyof R]: Partial<TableFooterColumn<R>>;
    };
    rowOrder?: Array<keyof R | FooterCalculation>;
    emptyContent?: string;
  };
}

interface TableFooterColumn<R> {
  [key: FooterCalculation | string]: boolean | number | string | CalcFunc<R>;
}
interface CalcFunc<R> {
  (data: Array<R>, key: keyof R): number | string;
}
type FooterCalculation = 'sum' | 'avg' | 'count' | 'max' | 'min';

import { TableColumn } from './table-column.interface';
import { TableRow } from './table-row.interface';

export interface TableConfig<R = TableRow> {
  /** Disable sorting of table data, useful when sorting is handled externally e.g. server-side pagination. Table will still use sortOrder to visually show how the data is sorted. <p>**Default:** `false`</p> */
  disableTableSort?: boolean;
  /** Allows for a more mobile friendly layout by displaying columns as rows instead. This is accomplished with css by adding `table-mobile` class to table element and adding column headers as data labels to each row. <p>**Default:** `false`</p>*/
  mobileLayout?: boolean;
  /** Make row and/or column headers sticky by adding `gt-sticky-row-header` and `gt-sticky-column-header` to table to alter table behaviour using css. Please note that in order for headers to be sticky, table can't be placed inside element with overflow set to auto. <p>**Default:** `{row:false, column:false}`</p>*/
  stickyHeaders?: {
    row?: boolean;
    column?: boolean;
  };
  /** Add one or more CSS classes to the table element e.g. `table table-striped table-bordered`. <p>**Default:** `table`</p>*/

  class?: string;
  rows?: {
    [Property in keyof R]: TableColumn<R>;
  };
  columns?: {
    [Property in keyof R]: TableColumn<R>;
  };
  pagination?: {
    length?: number;
  };
  rowClick?: boolean;
  rowHover?: boolean;
  footer?: {
    headers?: {
      [key: FooterCalculation | string]: string | boolean;
    };
    columns?: {
      [Property in keyof R]: Partial<TableFooterColumn<R>>;
    };
    rowOrder?: Array<keyof R | FooterCalculation>;
    emptyContent?: string;
  };
}

interface TableFooterColumn<R> {
  [key: FooterCalculation | string]: boolean | number | string | CalcFunc<R>;
}
interface CalcFunc<R> {
  (data: Array<R>, key: keyof R): number | string;
}
type FooterCalculation = 'sum' | 'avg' | 'count' | 'max' | 'min';

export interface TableInfo {
  numberOfRecords: number;
  pageSize: number;
  pageTotal: number;
  lazyLoaded?: boolean;
}

import { TableRow } from './table-row.interface';
import { TableConfig } from './table-config.interface';
import { TableInfo } from './table-info.interface';

export interface TableMeta<R = TableRow> {
  data: Array<Array<R>>;
  config: TableConfig<R>;
  info: TableInfo;
}

export interface TableRow {
  [key: string]: number | string | boolean | any;
}

import { Component, OnInit, TemplateRef, ViewChild } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { TableConfig, GtDeltaComponent } from '@angular-generic-table/core';
import { UntypedFormBuilder } from '@angular/forms';
import { withLatestFrom } from 'rxjs/operators';
import { Story } from '@storybook/angular/types-6-0';
import { TRANSPOSE_SNIPPETS } from './transpose.snippets';
interface RawData {
  year: string;
  value: number;
}
interface YearData extends RawData {
  delta: number;
  deltaIndex: number;
  combined: number;
  deltaAbsolute: number;
}
@Component({
  selector: 'docs-transpose',
  template: `
    <form [formGroup]="reactiveForm">
      <div class="row gy-3 gx-3 align-items-end mb-3">
        <div class="form-group col-6 col-sm-auto">
          <label for="length_input">Number of rows</label>
          <input
            id="length_input"
            formControlName="length"
            type="number"
            class="form-control"
          />
        </div>
        <div class="form-group col-6 col-sm-auto">
          <label for="search_input">Search</label>
          <input
            id="search_input"
            formControlName="search"
            type="text"
            class="form-control"
          />
        </div>
        <div class="col-auto">
          <button
            class="btn btn-outline-primary w-100"
            (click)="simulateLoad()"
          >
            Simulate load
          </button>
        </div>
        <div class="col col-sm-auto">
          <button class="btn btn-outline-danger w-100" (click)="empty()">
            Empty
          </button>
        </div>
        <div class="col col-sm-auto">
          <button class="btn btn-outline-primary w-100" (click)="load()">
            Reset
          </button>
        </div>
        <div class="col col-sm-auto">
          <button class="btn btn-outline-primary w-100" (click)="transpose()">
            Transpose
          </button>
        </div>
      </div>
      <div class="overflow-auto">
        <angular-generic-table
          #table
          [data]="data"
          [config]="tableConfig$"
          [loading]="loading$"
          [search]="search$"
        >
          <div class="table-loading gt-skeleton-loader"></div>
          <div class="table-no-data alert alert-info mt-3">Table is empty</div>
        </angular-generic-table>
      </div>
      <angular-generic-table-pagination [table]="table">
      </angular-generic-table-pagination>
      <ng-template #delta let-index="index" let-data="data">
        <gt-delta [index]="index" [data]="data"></gt-delta>
      </ng-template>
      <ng-template #deltaAbsolute let-index="index" let-data="data">
        <gt-delta
          [index]="index"
          [data]="data"
          [deltaTemplate]="deltaTemplate"
        ></gt-delta>
      </ng-template>
      <ng-template #deltaIndex let-index="index" let-data="data">
        <gt-delta [index]="index" [data]="data" [baseIndex]="0"></gt-delta>
      </ng-template>
      <ng-template
        #combined
        let-index="index"
        let-data="data"
        let-row="row"
        let-col="col"
      >
        {{ row.value }}
        <ng-container *ngIf="index > 0">
          <gt-delta [index]="index" [data]="data"></gt-delta>
        </ng-container>
      </ng-template>
      <ng-template #deltaTemplate let-delta="delta">
        <span>{{ delta.absolute }}</span></ng-template
      >
      <docs-tabs [content]="SNIPPETS"></docs-tabs>
    </form>
  `,
  styles: [],
})
export class TransposeComponent implements OnInit {
  @ViewChild('delta', { static: true }) delta:
    | TemplateRef<GtDeltaComponent>
    | undefined;
  @ViewChild('deltaAbsolute', { static: true }) deltaAbsolute:
    | TemplateRef<GtDeltaComponent>
    | undefined;
  @ViewChild('deltaIndex', { static: true }) deltaIndex:
    | TemplateRef<GtDeltaComponent>
    | undefined;
  @ViewChild('combined', { static: true }) combined:
    | TemplateRef<GtDeltaComponent>
    | undefined;
  loading$ = new BehaviorSubject(false);
  reactiveForm = this.fb.group({
    length: [10],
    search: [''],
  });
  search$ = this.reactiveForm.get('search')?.valueChanges as Observable<string>;
  tableConfig$: BehaviorSubject<TableConfig<YearData>> = new BehaviorSubject<
    TableConfig<YearData>
  >({});
  data: Array<RawData> = [];

  constructor(private fb: UntypedFormBuilder) {}

  ngOnInit(): void {
    this.transpose();
    this.load();
    this.reactiveForm
      .get('length')
      ?.valueChanges.pipe(withLatestFrom(this.tableConfig$))
      .subscribe(([length, config]) => {
        length = +length;
        this.tableConfig$.next({
          ...config,
          pagination: { ...config.pagination, length },
        });
      });
  }

  simulateLoad(): void {
    this.loading$.next(true);
    // set loading state to false after 2 seconds
    setTimeout(() => this.loading$.next(false), 2000);
  }
  empty(): void {
    this.data = [];
  }
  load(): void {
    this.data = [
      {
        year: '2010',
        value: 15,
      },
      {
        year: '2011',
        value: 30,
      },
      {
        year: '2012',
        value: 25,
      },
      {
        year: '2013',
        value: 0,
      },
      {
        year: '2014',
        value: 40,
      },
      {
        year: '2015',
        value: 0,
      },
      {
        year: '2016',
        value: -5,
      },
      {
        year: '2018',
        value: 75,
      },
      {
        year: '2019',
        value: 100,
      },
      {
        year: '2020',
        value: 250,
      },
      {
        year: '2021',
        value: 50,
      },
      {
        year: '2022',
        value: 60,
      },
    ];
  }
  transpose(): void {
    if (this.tableConfig$.value.columns) {
      this.tableConfig$.next({
        stickyHeaders: {
          row: true,
          column: true,
        },
        rows: {
          year: {
            sortable: true,
            header: false,
            class: 'text-end',
          },
          value: {
            class: 'text-end',
          },
          delta: {
            header: 'Delta %',
            templateRef: this.delta,
            class: 'text-end',
          },
          deltaIndex: {
            header: 'Since inception %',
            templateRef: this.deltaIndex,
            class: 'text-end',
          },
          deltaAbsolute: {
            header: 'Delta',
            templateRef: this.deltaAbsolute,
            class: 'text-end',
          },
          combined: {
            header: 'Value with change',
            templateRef: this.combined,
            class: 'text-end text-nowrap',
          },
        },
      });
    } else {
      this.tableConfig$.next({
        stickyHeaders: {
          row: true,
          column: true,
        },
        columns: {
          year: {
            sortable: true,
          },
          value: {
            class: 'text-end',
          },
          delta: {
            header: 'Delta %',
            templateRef: this.delta,
            class: 'text-end',
          },
          deltaIndex: {
            header: 'Since inception %',
            templateRef: this.deltaIndex,
            class: 'text-end',
          },
          deltaAbsolute: {
            header: 'Delta',
            templateRef: this.deltaAbsolute,
            class: 'text-end',
          },
          combined: {
            header: 'Value with change',
            templateRef: this.combined,
            class: 'text-end text-nowrap',
          },
        },
        pagination: { length: this.reactiveForm.get('length')?.value },
      });
    }
  }
  SNIPPETS = TRANSPOSE_SNIPPETS;
}

export const Transpose: Story<TransposeComponent> = (
  args: TransposeComponent
) => ({
  props: args,
  component: TransposeComponent,
});

