import { Component, OnInit, TemplateRef, ViewChild } from '@angular/core';
import { Story } from '@storybook/angular/types-6-0';
import {
  TableConfig,
  TableRow,
  TableColumn,
} from '@angular-generic-table/core';
import { ReplaySubject } from 'rxjs';
import { CUSTOM_TEMPLATES_DOCS } from './custom-templates.snippets';

@Component({
  selector: 'docs-custom-templates',
  template: `
    <div class="overflow-auto">
      <angular-generic-table
        [data]="data"
        [config]="config$"
      ></angular-generic-table>
    </div>
    <ng-template #actions let-row="row" let-col="col" let-index="index">
      <button
        class="btn btn-outline-primary btn-sm my-sm-n3 text-nowrap"
        (click)="clickAction(row, col, index)"
      >
        Click me!
      </button>
    </ng-template>
    <ng-template #color let-row="row" let-col="col">
      <div
        [style.background]="row[col.key]"
        style="width: 1.5rem; height: 1.5rem; border-radius: 50%"
      ></div>
    </ng-template>
    {{ clicked }}
    <docs-tabs [content]="SNIPPETS"></docs-tabs>
  `,
})
export class CustomTemplatesComponent implements OnInit {
  @ViewChild('actions', { static: true }) actions: TemplateRef<any> | undefined;
  @ViewChild('color', { static: true }) color: TemplateRef<any> | undefined;
  clicked = '';

  data = [
    {
      firstName: 'Peter',
      lastName: 'Parker',
      gender: 'male',
      favoriteColor: '#26BFAF',
      favoriteFood: 'Pasta',
    },
    {
      firstName: 'Mary Jane',
      lastName: 'Watson',
      gender: 'female',
      favoriteColor: '#0f0',
      favoriteFood: 'Pizza',
    },
  ];
  config$: ReplaySubject<TableConfig> = new ReplaySubject(1);

  SNIPPETS = CUSTOM_TEMPLATES_DOCS;
  ngOnInit(): void {
    this.config$.next({
      columns: {
        firstName: {},
        lastName: {},
        gender: {},
        favoriteColor: {
          templateRef: this.color,
        },
        favoriteFood: {},
        action: {
          templateRef: this.actions,
        },
      },
    });
  }
  clickAction(
    row: TableRow,
    column: { key: string; value: TableColumn },
    index: number
  ): void {
    console.log('clicked row:', row, 'col:', column);
    this.clicked = `clicked row number: ${index}`;
  }
}

export const CustomTemplates: Story<CustomTemplatesComponent> = (
  args: CustomTemplatesComponent
) => ({
  props: args,
  component: CustomTemplatesComponent,
});

import { Component, OnInit, TemplateRef, ViewChild } from '@angular/core';
import { Story } from '@storybook/angular/types-6-0';
import { SIMPLE_SNIPPETS } from './simple.snippets';
import { TableConfig, TableRow } from '@angular-generic-table/core';
import { DecimalPipe } from '@angular/common';

@Component({
  selector: 'docs-footer',
  template: `
    <div class="overflow-auto">
      <angular-generic-table [data]="data" [config]="config">
      </angular-generic-table>
    </div>
    <docs-tabs [content]="SNIPPETS"></docs-tabs>
    <ng-template #heightTmplRef let-row="row" let-col="col">
      <ng-container *ngIf="row[col.key || col] as height"
        >{{ height }} m
      </ng-container>
    </ng-template>
  `,
})
export class FooterComponent implements OnInit {
  @ViewChild('heightTmplRef', { static: true }) heightTmplRef:
    | TemplateRef<any>
    | undefined;
  data = [
    {
      firstName: 'Peter',
      lastName: 'Parker',
      gender: 'male',
      favoriteFood: 'Pasta',
      age: 27,
      weight: 85.457,
      height: 1.85,
    },
    {
      firstName: 'Mary Jane',
      lastName: 'Watson',
      gender: 'female',
      favoriteFood: 'Pizza',
      age: 25,
      weight: 60.123,
      height: 1.65,
    },
  ];
  config: TableConfig = {};

  ngOnInit() {
    this.config = {
      mobileLayout: true,
      columns: {
        firstName: {
          mobileHeader: true,
        },
        lastName: {
          mobileHeader: true,
        },
        gender: {
          mobileHeader: true,
        },
        favoriteFood: {
          mobileHeader: true,
          class: 'text-end flex-end',
        },
        age: {
          mobileHeader: true,
          class: 'text-end',
        },
        weight: {
          mobileHeader: true,
          class: 'text-end',
          transform: {
            pipe: DecimalPipe,
            args: ['1.0-2'],
          },
        },
        height: {
          mobileHeader: true,
          class: 'text-end',
          templateRef: this.heightTmplRef,
        },
      },
      footer: {
        headers: {
          sum: 'Total',
          numberOfWomen: 'Number of women',
          numberOfMen: 'Number of men',
          min: true,
          max: true,
          avg: true,
          count: true,
          static: true,
          first: true,
        },
        rowOrder: [
          'first',
          'numberOfWomen',
          'numberOfMen',
          'min',
          'max',
          'sum',
          'avg',
          'count',
        ],
        columns: {
          firstName: {},
          lastName: {
            static: 'n/a',
          },
          gender: {
            numberOfWomen: (data: Array<TableRow>, key) => {
              let count = 0;
              for (let i = 0; i < data.length; i++) {
                if (data[i][key] === 'female') {
                  count++;
                }
              }
              return count;
            },
            numberOfMen: (data: Array<TableRow>, key) => {
              let count = 0;
              for (let i = 0; i < data.length; i++) {
                if (data[i][key] === 'male') {
                  count++;
                }
              }
              return count;
            },
          },
          favoriteFood: {
            first: (data: Array<TableRow>, key) => data[0][key],
          },
          age: {
            sum: true,
            avg: true,
            count: true,
            max: true,
            min: true,
          },
          weight: {
