  mobileLayout$ = new BehaviorSubject(true);
  data = [
    {
      firstName: 'Peter',
      lastName: 'Parker',
      gender: 'male',
      favoriteFood: 'Pasta',
    },
    {
      firstName: 'Mary Jane',
      lastName: 'Watson',
      gender: 'female',
      favoriteFood: 'Pizza',
    },
  ];
  config$: Observable<TableConfig> = this.mobileLayout$.pipe(
    map((mobileLayout) => ({
      mobileLayout,
      columns: {
        firstName: {
          mobileHeader: true,
          sortable: true,
        },
        lastName: {
          mobileHeader: true,
          sortable: true,
        },
        gender: {
          mobileHeader: true,
          transform: {
            pipe: GenderPipe,
          },
        },
        favoriteFood: {
          mobileHeader: true,
        },
        action: {
          mobileHeader: false,
          header: false,
          templateRef: this.actions,
        },
      },
    }))
  );

  SNIPPETS = MOBILE_LAYOUT_SNIPPETS;

  toggleLayout = (): void => {
    this.mobileLayout$.next(!this.mobileLayout$.getValue());
  };
  clickAction(
    row: TableRow,
    column: { key: string; value: TableColumn },
    index: number
  ): void {
    console.log('clicked row:', row, 'col:', column);
    this.clicked = `Clicked row number: ${index}`;
  }
}

