          transform: {
            pipe: DatePipe,
            args: ['longDate'],
          },
        },
      },
      pagination: {
        length: this.paginationForm.get('length')?.value || 0,
      },
    });
  }
}

export const Pagination: Story<PaginationComponent> = (
  args: PaginationComponent
) => ({
  props: args,
  component: PaginationComponent,
});

