(
  keys: GtSortOrder
): ((a: TableRow, b: TableRow) => number) => {
  const order = keys.map((key) => (key.order === 'desc' ? -1 : 1));
  return (a, b) => {
    for (let i = 0; i < keys.length; i++) {
      const o = keys[i].key;
      if (a[o] > b[o]) return order[i];
      if (a[o] < b[o]) return -order[i];
    }
    return 0;
  };
}

(
  sortConfig: GtSortEvent | GtSortConfig
): string => {
  const order = sortConfig.order === 'desc' ? '-' : '+';
  return `${order}${sortConfig.key}`;
}

(sortOrder: GtSortOrder): string => {
  return sortOrder
    .map((sortConfig) => sortOrderConfigToParam(sortConfig))
    .join(',');
}

