import { Pipe, PipeTransform } from '@angular/core';
import { capitalize } from '../utilities/utilities';

@Pipe({
  name: 'capitalCase',
  standalone: true,
})
export class CapitalCasePipe implements PipeTransform {
  transform(s: string): any {
    return capitalize(s);
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { dashed } from '../utilities/utilities';

@Pipe({
  name: 'dashCase',
  standalone: true,
})
export class DashCasePipe implements PipeTransform {
  transform(s: string): any {
    return dashed(s);
  }
}

import { Injector, Pipe, PipeTransform, Type } from '@angular/core';

@Pipe({
  name: 'dynamicPipe',
  standalone: true,
})
export class DynamicPipe implements PipeTransform {
  constructor(private injector: Injector) {}

  transform(value: any, requiredPipe: Type<any>, pipeArgs: any[]): any {
    const injector = Injector.create({
      name: 'DynamicPipe',
      parent: this.injector,
      providers: [{ provide: requiredPipe }],
    });
    const pipe = injector.get(requiredPipe);
    return pipe.transform(value, ...(pipeArgs || []));
  }
}

import {
  Component,
  Pipe,
  PipeTransform,
  TemplateRef,
  ViewChild,
  ViewEncapsulation,
} from '@angular/core';
import { Story } from '@storybook/angular/types-6-0';
import {
  TableColumn,
  TableConfig,
  TableRow,
} from '@angular-generic-table/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { MOBILE_LAYOUT_SNIPPETS } from './mobileLayout.snippets';

@Pipe({
  name: 'genderPipe',
})
export class GenderPipe implements PipeTransform {
  transform(gender: 'male' | 'female'): string {
    return gender === 'male' ? '👨' : '👩';
  }
}

@Component({
  selector: 'docs-mobile-layout',
  template: `
    <div class="d-flex justify-content-end mb-1 align-items-center">
      {{ clicked }}
      <button class="btn btn-link d-sm-none " (click)="toggleLayout()">
        {{ (mobileLayout$ | async) ? 'Desktop ' : 'Mobile ' }} layout
      </button>
    </div>
    <div [class.overflow-auto]="(mobileLayout$ | async) === false">
      <angular-generic-table
        [data]="data"
        [config]="config$"
      ></angular-generic-table>
    </div>
    <ng-template #actions let-row="row" let-col="col" let-index="index">
      <button
        class="btn btn-outline-primary btn-sm my-sm-n3 text-nowrap"
        (click)="clickAction(row, col, index)"
      >
        Click me!
      </button>
    </ng-template>
    <docs-tabs [content]="SNIPPETS"></docs-tabs>
  `,
  styles: [
    `
      .table th {
        white-space: nowrap;
      }
    `,
  ],
  encapsulation: ViewEncapsulation.None,
})
export class MobileLayoutComponent {
  @ViewChild('actions', { static: true }) actions: TemplateRef<any> | undefined;
  clicked = '';

  mobileLayout$ = new BehaviorSubject(true);
  data = [
    {
      firstName: 'Peter',
      lastName: 'Parker',
      gender: 'male',
      favoriteFood: 'Pasta',
    },
    {
      firstName: 'Mary Jane',
      lastName: 'Watson',
      gender: 'female',
      favoriteFood: 'Pizza',
    },
  ];
  config$: Observable<TableConfig> = this.mobileLayout$.pipe(
    map((mobileLayout) => ({
      mobileLayout,
      columns: {
        firstName: {
          mobileHeader: true,
          sortable: true,
        },
        lastName: {
          mobileHeader: true,
          sortable: true,
        },
        gender: {
          mobileHeader: true,
          transform: {
            pipe: GenderPipe,
          },
        },
        favoriteFood: {
          mobileHeader: true,
        },
        action: {
          mobileHeader: false,
          header: false,
          templateRef: this.actions,
        },
      },
    }))
  );

  SNIPPETS = MOBILE_LAYOUT_SNIPPETS;

  toggleLayout = (): void => {
    this.mobileLayout$.next(!this.mobileLayout$.getValue());
  };
  clickAction(
    row: TableRow,
    column: { key: string; value: TableColumn },
    index: number
  ): void {
    console.log('clicked row:', row, 'col:', column);
    this.clicked = `Clicked row number: ${index}`;
  }
}

export const Mobile: Story<MobileLayoutComponent> = (
  args: MobileLayoutComponent
) => ({
  props: args,
  component: MobileLayoutComponent,
});

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'highlight',
  standalone: true,
})
export class HighlightPipe implements PipeTransform {
  transform(text: any, searchTerm: string | null): string {
    if (!searchTerm) {
      return text;
    }
    const haystackAlwaysString = text + '';
    let highlightedText = haystackAlwaysString; // fallback

    let searchPattern;
    try {
      searchPattern = new RegExp(
        '(' +
          // @ts-ignore
          searchTerm
            .toLowerCase()
            .match(/".*?"|[^ ]+/g) // extract words
            .map(
              (needle) => needle.replace(/"(.*?)"/, '$1') // strip away '"'
            )
            .join('|') + // combine words
          ')',
        'ig'
      );
    } catch (error) {
      return highlightedText;
    }

    const containsTagPattern = /(<.*?>)(.*)(<\/.*?>)/gi;
    const containsTagMatches = containsTagPattern.exec(haystackAlwaysString);

    if (containsTagMatches) {
      // tag exists in haystack
      highlightedText =
        containsTagMatches[1] +
        containsTagMatches[2].replace(
          searchPattern,
          '<span class="gt-highlight-search">$1</span>'
        ) +
        containsTagMatches[3];
    } else {
      highlightedText = haystackAlwaysString.replace(
        searchPattern,
        '<span class="gt-highlight-search">$1</span>'
      );
    }

    return highlightedText;
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { TableRow } from '../models/table-row.interface';

@Pipe({
  name: 'rowSelection',
  standalone: true,
})
export class RowSelectionPipe implements PipeTransform {
  transform(
    row: TableRow,
    selection: any,
    comparator: (row: TableRow, selection: any) => boolean,
    className?: string
  ): string {
    return className && comparator(row, selection) ? className : '';
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { GtSortOrder } from '../models/table-sort.interface';

@Pipe({
  name: 'sortClass',
  standalone: true,
})
export class SortClassPipe implements PipeTransform {
  transform(
    sortOrder: GtSortOrder | null,
    key: string,
    context: 'class' | 'aria' | 'order' = 'class'
  ): string | null {
    const sortIndex = sortOrder
      ? sortOrder.findIndex((s) => s.key === key)
      : -1;
    if (context === 'aria') {
      if (sortIndex === -1 || !sortOrder) {
        return null;
      } else {
        return `${sortOrder[sortIndex].order}ending`;
      }
    } else if (context === 'class') {
      if (sortIndex === -1 || !sortOrder) {
        return '';
      } else {
        return `gt-sort-${sortOrder[sortIndex].order}`;
      }
    } else {
      return (sortOrder && sortOrder?.length === 1) || sortIndex < 0
        ? null
        : sortIndex + 1 + '';
    }
  }
}

