export const Simple: Story<SimpleComponent> = (args: SimpleComponent) => ({
  props: args,
  component: SimpleComponent,
});

import { TableColumn } from './table-column.interface';
import { TableRow } from './table-row.interface';

export interface TableConfig<R = TableRow> {
  /** Disable sorting of table data, useful when sorting is handled externally e.g. server-side pagination. Table will still use sortOrder to visually show how the data is sorted. <p>**Default:** `false`</p> */
  disableTableSort?: boolean;
  /** Allows for a more mobile friendly layout by displaying columns as rows instead. This is accomplished with css by adding `table-mobile` class to table element and adding column headers as data labels to each row. <p>**Default:** `false`</p>*/
  mobileLayout?: boolean;
  /** Make row and/or column headers sticky by adding `gt-sticky-row-header` and `gt-sticky-column-header` to table to alter table behaviour using css. Please note that in order for headers to be sticky, table can't be placed inside element with overflow set to auto. <p>**Default:** `{row:false, column:false}`</p>*/
  stickyHeaders?: {
    row?: boolean;
    column?: boolean;
  };
  /** Add one or more CSS classes to the table element e.g. `table table-striped table-bordered`. <p>**Default:** `table`</p>*/
  class?: string;
  rows?: {
    [Property in keyof R]: TableColumn<R>;
  };
  columns?: {
    [Property in keyof R]: TableColumn<R>;
  };
  pagination?: {
    length?: number;
  };
  rowClick?: boolean;
  /** Toggle row active state on mouse enter/leave (hover) <p>**Default:** `false`</p>*/
  activateRowOnHover?: boolean;
  /** Toggle row active state on keyboard navigation <p>**Default:** `false`</p>*/
  activateRowOnKeyboardNavigation?: boolean;
  deactivateRowOnLostFocus?: boolean;
  footer?: {
    headers?: {
      [key: FooterCalculation | string]: string | boolean;
    };
    columns?: {
      [Property in keyof R]: Partial<TableFooterColumn<R>>;
    };
    rowOrder?: Array<keyof R | FooterCalculation>;
    emptyContent?: string;
  };
}

interface TableFooterColumn<R> {
  [key: FooterCalculation | string]: boolean | number | string | CalcFunc<R>;
}
interface CalcFunc<R> {
  (data: Array<R>, key: keyof R): number | string;
}
type FooterCalculation = 'sum' | 'avg' | 'count' | 'max' | 'min';

import { NESTED_SNIPPETS } from './nested.snippets';
import { Story } from '@storybook/angular/types-6-0';
import { Component, OnInit, TemplateRef, ViewChild } from '@angular/core';
import { TableConfig } from '@angular-generic-table/core';
interface NestedData {
  name: {
    first: string;
    last: string;
  };
  data: {
    details?: {
      gender: 'male' | 'female';
      favoriteFood: 'Pasta' | 'Pizza';
    };
  };
}

interface Data {
  firstName: string;
  lastName: string;
  gender: 'male' | 'female';
  favoriteFood: 'Pasta' | 'Pizza';
  missing: string;
}
@Component({
  selector: 'nested-data',
  template: `
    <div class="row gy-3">
      <div class="col col-sm-auto">
        <button class="btn btn-outline-primary mb-3" (click)="loadData()">
          Load other data
        </button>
      </div>
      <div class="col col-sm-auto">
        <button class="btn btn-outline-primary mb-3" (click)="resetData()">
          Reset
        </button>
      </div>
    </div>
    <div class="overflow-auto">
      <angular-generic-table
        [data]="data"
        [config]="config"
      ></angular-generic-table>
    </div>
    <docs-tabs [content]="SNIPPETS"></docs-tabs>
    <ng-template #gender let-row="row" let-col="col">
      <div [ngSwitch]="row[col.key]">
        <ng-container *ngSwitchCase="'male'">♂️</ng-container>
        <ng-container *ngSwitchCase="'female'">♀️</ng-container>
      </div>
    </ng-template>
  `,
  styles: [],
})
export class NestedDataComponent implements OnInit {
  @ViewChild('gender', { static: true }) gender: TemplateRef<any> | undefined;

  config: TableConfig<Data> = {};
  data: Array<NestedData> = [];
  ngOnInit(): void {
    this.resetData();
    this.config = {
      columns: {
        firstName: {
          sortable: true,
          mapTo: { path: 'name.first' },
        },
        lastName: {
          sortable: true,
          mapTo: { path: 'name.last' },
        },
        gender: {
          mapTo: { path: 'data.details.gender' },
          templateRef: this.gender,
        },
        favoriteFood: {
          sortable: true,
          mapTo: { path: 'data.details.favoriteFood', missingValue: 'n/a' },
        },
        missing: {
          mapTo: { path: 'data.missingKey.noMatch', missingValue: 'n/a' },
        },
      },
    };
  }

  resetData() {
    this.data = [
      {
        name: {
          first: 'Peter',
          last: 'Parker',
        },
        data: {
          details: {
            gender: 'male',
            favoriteFood: 'Pasta',
          },
        },
      },
      {
        name: {
          first: 'Mary Jane',
          last: 'Watson',
        },
        data: {
          details: {
            gender: 'female',
            favoriteFood: 'Pizza',
          },
        },
      },
    ];
  }

  loadData(): void {
    this.data = [
      {
        name: {
          first: 'John',
          last: 'Doe',
        },
        data: {
          details: {
            gender: 'male',
            favoriteFood: 'Pasta',
          },
        },
      },
      {
        name: {
          first: 'Jane',
          last: 'Doe',
        },
        data: {
          details: {
            gender: 'female',
            favoriteFood: 'Pizza',
          },
        },
      },
      {
        name: {
          first: 'Foo',
          last: 'Bar',
        },
        data: {},
      },
    ];
  }

  SNIPPETS = NESTED_SNIPPETS;
}

export const Nested: Story<NestedDataComponent> = (
  args: NestedDataComponent
) => ({
  props: args,
  component: NestedDataComponent,
});

import {
  ChangeDetectionStrategy,
  Component,
  Input,
  OnChanges,
  SimpleChanges,
  TemplateRef,
} from '@angular/core';
import { TableRow } from '../models/table-row.interface';
import { NgIf, NgTemplateOutlet, PercentPipe } from '@angular/common';

export interface GtDelta {
  relative: number | string;
  absolute: number;
}
@Component({
  selector: 'gt-delta',
  template: `<span
      *ngIf="value as delta"
      [class]="[
        classes.span,
        delta.absolute > 0
          ? classes.positive
          : delta.absolute < 0
          ? classes.negative
          : null
      ]"
      [class.gt-delta-positive]="delta.absolute > 0"
      [class.gt-delta-negative]="delta.absolute < 0"
      ><ng-container
        *ngTemplateOutlet="deltaTemplate || defaultTemplate; context: { delta }"
      ></ng-container>
    </span>
    <ng-template #defaultTemplate let-delta="delta">
      <span *ngIf="delta.relative">{{
        delta.relative | percent: '1.0-2'
      }}</span>
    </ng-template>`,
  styles: [
    `
      :host {
        display: inline-block;
      }
    `,
  ],
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [PercentPipe, NgIf, NgTemplateOutlet],
})
export class GtDeltaComponent implements OnChanges {
  get value() {
    return this._value;
  }

  set value(value) {
    this._value = value;
  }
  get deltaTemplate(): TemplateRef<any> {
    return this._deltaTemplate;
  }
  constructor() {}
  @Input() set deltaTemplate(deltaTemplate: TemplateRef<any>) {
    this._deltaTemplate = deltaTemplate;
  }
  Math = Math;
  Number = Number;
  @Input() data: Array<TableRow> = [];
  @Input() index: number = 0;
  @Input() baseIndex?: number;
  @Input() classes = {
    span: 'gt-delta',
    positive: 'text-success',
    negative: 'text-danger',
  };
  @Input() key: string = 'value';
  @Input() notApplicableValue: string | null = null;
  @Input() initialValue: string | number | null = null;
  private _value?: GtDelta;
  private _deltaTemplate?: any;
  ngOnChanges(changes: SimpleChanges): void {
    if (!changes.data.currentValue) {
      return;
    }
    const data = changes.data?.currentValue;
    const index = changes.index?.currentValue || this.index;
    const baseIndex = changes.baseIndex?.currentValue;
    const key = changes.key?.currentValue || this.key;
    const initialValue =
      changes.initialValue?.currentValue || this.initialValue;

    const deltaValue =
      index === 0
        ? initialValue
        : data[index][key] -
          (baseIndex === undefined
            ? data[index - 1][key]
            : data[baseIndex][key]);
    const baseValue =
      index === 0
        ? 1
        : baseIndex === undefined
        ? data[index - 1][key]
        : data[baseIndex][key];

    const relative =
      index === 0
        ? initialValue
        : Math.sign(deltaValue) * Math.abs(deltaValue / baseValue);

    this.value = {
      relative: Number.isFinite(relative)
        ? relative
        : index === 0
        ? initialValue
        : changes.notApplicableValue?.currentValue || this.notApplicableValue,
      absolute: deltaValue,
    };
  }
}

