import { Component, OnInit, TemplateRef, ViewChild } from '@angular/core';
import { Story } from '@storybook/angular/types-6-0';
import {
  TableConfig,
  TableRow,
  TableColumn,
} from '@angular-generic-table/core';
import { ReplaySubject } from 'rxjs';
import { CUSTOM_TEMPLATES_DOCS } from './custom-templates.snippets';

@Component({
  selector: 'docs-custom-templates',
  template: `
    <div class="overflow-auto">
      <angular-generic-table
        [data]="data"
        [config]="config$"
      ></angular-generic-table>
    </div>
    <ng-template #actions let-row="row" let-col="col" let-index="index">
      <button
        class="btn btn-outline-primary btn-sm my-sm-n3 text-nowrap"
        (click)="clickAction(row, col, index)"
      >
        Click me!
      </button>
    </ng-template>
    <ng-template #color let-row="row" let-col="col">
      <div
        [style.background]="row[col.key]"
        style="width: 1.5rem; height: 1.5rem; border-radius: 50%"
      ></div>
    </ng-template>
    {{ clicked }}
    <docs-tabs [content]="SNIPPETS"></docs-tabs>
  `,
})
export class CustomTemplatesComponent implements OnInit {
  @ViewChild('actions', { static: true }) actions: TemplateRef<any> | undefined;
  @ViewChild('color', { static: true }) color: TemplateRef<any> | undefined;
  clicked = '';

  data = [
    {
      firstName: 'Peter',
      lastName: 'Parker',
      gender: 'male',
      favoriteColor: '#26BFAF',
      favoriteFood: 'Pasta',
    },
    {
      firstName: 'Mary Jane',
      lastName: 'Watson',
      gender: 'female',
      favoriteColor: '#0f0',
      favoriteFood: 'Pizza',
    },
  ];
  config$: ReplaySubject<TableConfig> = new ReplaySubject(1);

  SNIPPETS = CUSTOM_TEMPLATES_DOCS;
  ngOnInit(): void {
    this.config$.next({
      columns: {
        firstName: {},
        lastName: {},
        gender: {},
        favoriteColor: {
          templateRef: this.color,
        },
        favoriteFood: {},
        action: {
          templateRef: this.actions,
        },
      },
    });
  }
  clickAction(
    row: TableRow,
    column: { key: string; value: TableColumn },
    index: number
  ): void {
    console.log('clicked row:', row, 'col:', column);
    this.clicked = `clicked row number: ${index}`;
  }
}

export const CustomTemplates: Story<CustomTemplatesComponent> = (
  args: CustomTemplatesComponent
) => ({
  props: args,
  component: CustomTemplatesComponent,
});

import { Component, OnInit, TemplateRef, ViewChild } from '@angular/core';
import { Story } from '@storybook/angular/types-6-0';
import { SIMPLE_SNIPPETS } from './simple.snippets';
import { TableConfig, TableRow } from '@angular-generic-table/core';
import { DecimalPipe } from '@angular/common';

@Component({
  selector: 'docs-footer',
  template: `
    <div class="overflow-auto">
      <angular-generic-table [data]="data" [config]="config">
      </angular-generic-table>
    </div>
    <docs-tabs [content]="SNIPPETS"></docs-tabs>
    <ng-template #heightTmplRef let-row="row" let-col="col">
      <ng-container *ngIf="row[col.key || col] as height"
        >{{ height }} m
      </ng-container>
    </ng-template>
  `,
})
export class FooterComponent implements OnInit {
  @ViewChild('heightTmplRef', { static: true }) heightTmplRef:
    | TemplateRef<any>
    | undefined;
  data = [
    {
      firstName: 'Peter',
      lastName: 'Parker',
      gender: 'male',
      favoriteFood: 'Pasta',
      age: 27,
      weight: 85.457,
      height: 1.85,
    },
    {
      firstName: 'Mary Jane',
      lastName: 'Watson',
      gender: 'female',
      favoriteFood: 'Pizza',
      age: 25,
      weight: 60.123,
      height: 1.65,
    },
  ];
  config: TableConfig = {};

  ngOnInit() {
    this.config = {
      mobileLayout: true,
      columns: {
        firstName: {
          mobileHeader: true,
        },
        lastName: {
          mobileHeader: true,
        },
        gender: {
          mobileHeader: true,
        },
        favoriteFood: {
          mobileHeader: true,
          class: 'text-end flex-end',
        },
        age: {
          mobileHeader: true,
          class: 'text-end',
        },
        weight: {
          mobileHeader: true,
          class: 'text-end',
          transform: {
            pipe: DecimalPipe,
            args: ['1.0-2'],
          },
        },
        height: {
          mobileHeader: true,
          class: 'text-end',
          templateRef: this.heightTmplRef,
        },
      },
      footer: {
        headers: {
          sum: 'Total',
          numberOfWomen: 'Number of women',
          numberOfMen: 'Number of men',
          min: true,
          max: true,
          avg: true,
          count: true,
          static: true,
          first: true,
        },
        rowOrder: [
          'first',
          'numberOfWomen',
          'numberOfMen',
          'min',
          'max',
          'sum',
          'avg',
          'count',
        ],
        columns: {
          firstName: {},
          lastName: {
            static: 'n/a',
          },
          gender: {
            numberOfWomen: (data: Array<TableRow>, key) => {
              let count = 0;
              for (let i = 0; i < data.length; i++) {
                if (data[i][key] === 'female') {
                  count++;
                }
              }
              return count;
            },
            numberOfMen: (data: Array<TableRow>, key) => {
              let count = 0;
              for (let i = 0; i < data.length; i++) {
                if (data[i][key] === 'male') {
                  count++;
                }
              }
              return count;
            },
          },
          favoriteFood: {
            first: (data: Array<TableRow>, key) => data[0][key],
          },
          age: {
            sum: true,
            avg: true,
            count: true,
            max: true,
            min: true,
          },
          weight: {
            sum: true,
            avg: true,
            min: true,
          },
          height: {
            avg: true,
            min: true,
            max: true,
          },
        },
      },
    };
  }

  SNIPPETS = SIMPLE_SNIPPETS;
}

export const Footer: Story<FooterComponent> = (args: FooterComponent) => ({
  props: args,
  component: FooterComponent,
});

import {
  ChangeDetectionStrategy,
  Component,
  Input,
  OnChanges,
  SimpleChanges,
  TemplateRef,
} from '@angular/core';
import { TableRow } from '../models/table-row.interface';
import { NgIf, NgTemplateOutlet, PercentPipe } from '@angular/common';

export interface GtDelta {
  relative: number | string;
  absolute: number;
}
@Component({
  selector: 'gt-delta',
  template: `<span
      *ngIf="value as delta"
      [class]="[
        classes.span,
        delta.absolute > 0
          ? classes.positive
          : delta.absolute < 0
          ? classes.negative
          : null
      ]"
      [class.gt-delta-positive]="delta.absolute > 0"
      [class.gt-delta-negative]="delta.absolute < 0"
      ><ng-container
        *ngTemplateOutlet="deltaTemplate || defaultTemplate; context: { delta }"
      ></ng-container>
    </span>
    <ng-template #defaultTemplate let-delta="delta">
      <span *ngIf="delta.relative">{{
        delta.relative | percent: '1.0-2'
      }}</span>
    </ng-template>`,
  styles: [
    `
      :host {
        display: inline-block;
      }
    `,
  ],
  changeDetection: ChangeDetectionStrategy.OnPush,
  standalone: true,
  imports: [PercentPipe, NgIf, NgTemplateOutlet],
})
export class GtDeltaComponent implements OnChanges {
  get value() {
    return this._value;
  }

  set value(value) {
    this._value = value;
  }
  get deltaTemplate(): TemplateRef<any> {
    return this._deltaTemplate;
  }
  constructor() {}
  @Input() set deltaTemplate(deltaTemplate: TemplateRef<any>) {
    this._deltaTemplate = deltaTemplate;
  }
  Math = Math;
  Number = Number;
  @Input() data: Array<TableRow> = [];
  @Input() index: number = 0;
  @Input() baseIndex?: number;
  @Input() classes = {
    span: 'gt-delta',
    positive: 'text-success',
    negative: 'text-danger',
  };
  @Input() key: string = 'value';
  @Input() notApplicableValue: string | null = null;
  @Input() initialValue: string | number | null = null;
  private _value?: GtDelta;
  private _deltaTemplate?: any;
  ngOnChanges(changes: SimpleChanges): void {
    if (!changes.data.currentValue) {
      return;
    }
    const data = changes.data?.currentValue;
    const index = changes.index?.currentValue || this.index;
    const baseIndex = changes.baseIndex?.currentValue;
    const key = changes.key?.currentValue || this.key;
    const initialValue =
      changes.initialValue?.currentValue || this.initialValue;

    const deltaValue =
      index === 0
        ? initialValue
        : data[index][key] -
          (baseIndex === undefined
            ? data[index - 1][key]
            : data[baseIndex][key]);
    const baseValue =
      index === 0
        ? 1
        : baseIndex === undefined
        ? data[index - 1][key]
        : data[baseIndex][key];

    const relative =
      index === 0
        ? initialValue
        : Math.sign(deltaValue) * Math.abs(deltaValue / baseValue);

    this.value = {
      relative: Number.isFinite(relative)
        ? relative
        : index === 0
        ? initialValue
        : changes.notApplicableValue?.currentValue || this.notApplicableValue,
      absolute: deltaValue,
    };
  }
}

import { Component, OnInit, TemplateRef, ViewChild } from '@angular/core';
import { Story } from '@storybook/angular/types-6-0';
import { HORIZONTAL_TABLE_SNIPPETS } from './horizontal-table.snippets';
import { GtDeltaComponent, TableConfig } from '@angular-generic-table/core';
import { BehaviorSubject } from 'rxjs';

@Component({
  selector: 'docs-horizontal-table',
  template: `
    <button class="btn btn-outline-primary" (click)="simulateLoad()">
      Simulate load
    </button>
    <button class="btn btn-outline-danger mx-3" (click)="empty()">Empty</button>
    <button class="btn btn-outline-primary" (click)="load()">Reset</button>
    <div class="overflow-auto">
      <angular-generic-table
        [data]="data"
        [config]="config"
        [loading]="loading$"
      >
        <div class="table-loading gt-skeleton-loader"></div>
        <div class="table-no-data alert alert-info mt-3">Table is empty</div>
      </angular-generic-table>
    </div>
    <ng-template #feelings let-row="row" let-col="col">
      <div [ngSwitch]="row[col.key]">
        <ng-container *ngSwitchCase="'thrilled'">😀</ng-container>
        <ng-container *ngSwitchCase="'positive'">🙂</ng-container>
        <ng-container *ngSwitchCase="'neutral'">😐</ng-container>
        <ng-container *ngSwitchCase="'negative'">😭</ng-container>
      </div>
    </ng-template>
    <ng-template #delta let-data="data" let-index="index">
      <gt-delta [index]="index" [data]="data"></gt-delta>
    </ng-template>
    <ng-template #deltaIndex let-data="data" let-index="index">
      <gt-delta [index]="index" [baseIndex]="0" [data]="data"></gt-delta>
    </ng-template>
    <docs-tabs [content]="SNIPPETS"></docs-tabs>
  `,
  styles: [],
})
export class HorizontalTableComponent implements OnInit {
  @ViewChild('feelings', { static: true }) feelings:
    | TemplateRef<any>
    | undefined;
  @ViewChild('delta', { static: true }) delta:
    | TemplateRef<GtDeltaComponent>
    | undefined;
  @ViewChild('deltaIndex', { static: true }) deltaIndex:
    | TemplateRef<GtDeltaComponent>
    | undefined;
  loading$ = new BehaviorSubject(false);
  config: TableConfig = {};
  data: any = [];

  ngOnInit(): void {
    this.config = {
      stickyHeaders: {
        row: true,
      },
      mobileLayout: true,
      rows: {
        year: {
          class: 'text-end',
          header: false,
        },
        value: {
          class: 'text-end',
        },
        delta: {
          header: 'Delta %',
          templateRef: this.delta,
          class: 'text-end',
        },
        deltaIndex: {
          header: 'Since inception %',
          templateRef: this.deltaIndex,
          class: 'text-end',
        },
        feeling: {
          templateRef: this.feelings,
          class: 'text-end',
        },
      },
    };
    this.load();
  }
  simulateLoad(): void {
    this.loading$.next(true);
    // set loading state to false after 2 seconds
    setTimeout(() => this.loading$.next(false), 2000);
  }
  empty(): void {
    this.data = [];
  }
  load(): void {
    this.data = [
      {
        year: '2017',
        value: 50,
        feeling: 'neutral',
      },
      {
        year: '2018',
        value: 75,
        feeling: 'positive',
      },
      {
        year: '2019',
        value: 100,
        feeling: 'thrilled',
      },
      {
        year: '2020',
        value: 250,
        feeling: 'thrilled',
      },
      {
        year: '2021',
        value: 50,
        feeling: 'negative',
      },
    ];
  }

  SNIPPETS = HORIZONTAL_TABLE_SNIPPETS;
}

export const Horizontal: Story<HorizontalTableComponent> = (
  args: HorizontalTableComponent
) => ({
  props: args,
  component: HorizontalTableComponent,
});

