          mapTo: { path: 'data.missingKey.noMatch', missingValue: 'n/a' },
        },
      },
    };
  }

  resetData() {
    this.data = [
      {
        name: {
          first: 'Peter',
          last: 'Parker',
        },
        data: {
          details: {
            gender: 'male',
            favoriteFood: 'Pasta',
          },
        },
      },
      {
        name: {
          first: 'Mary Jane',
          last: 'Watson',
        },
        data: {
          details: {
            gender: 'female',
            favoriteFood: 'Pizza',
          },
        },
      },
    ];
  }

  loadData(): void {
    this.data = [
      {
        name: {
          first: 'John',
          last: 'Doe',
        },
        data: {
          details: {
            gender: 'male',
            favoriteFood: 'Pasta',
          },
        },
      },
      {
        name: {
          first: 'Jane',
          last: 'Doe',
        },
        data: {
          details: {
            gender: 'female',
            favoriteFood: 'Pizza',
          },
        },
      },
      {
        name: {
          first: 'Foo',
          last: 'Bar',
        },
        data: {},
      },
    ];
  }

  SNIPPETS = NESTED_SNIPPETS;
}

export const Nested: Story<NestedDataComponent> = (
  args: NestedDataComponent
) => ({
  props: args,
  component: NestedDataComponent,
});

