(sortParams: string): GtSortOrder => {
  const sortParamsArray = sortParams.split(',');
  return sortParamsArray.map((sortParam) => {
    const [key, order] = sortParam.split(':');
    return {
      key: key.replace(/^[+-]/, ''),
      order: order === 'desc' ? 'desc' : 'asc',
    };
  });
}

