      {
        year: '2021',
        value: 50,
      },
      {
        year: '2022',
        value: 60,
      },
    ];
  }
  transpose(): void {
    if (this.tableConfig$.value.columns) {
      this.tableConfig$.next({
        stickyHeaders: {
          row: true,
          column: true,
        },
        rows: {
          year: {
            sortable: true,
            header: false,
            class: 'text-end',
          },
          value: {
            class: 'text-end',
