@Component({
  selector: 'docs-mobile-layout',
  template: `
    <div class="d-flex justify-content-end mb-1 align-items-center">
      {{ clicked }}
      <button class="btn btn-link d-sm-none " (click)="toggleLayout()">
        {{ (mobileLayout$ | async) ? 'Desktop ' : 'Mobile ' }} layout
      </button>
    </div>
    <div [class.overflow-auto]="(mobileLayout$ | async) === false">
      <angular-generic-table
        [data]="data"
        [config]="config$"
      ></angular-generic-table>
    </div>
    <ng-template #actions let-row="row" let-col="col" let-index="index">
      <button
        class="btn btn-outline-primary btn-sm my-sm-n3 text-nowrap"
        (click)="clickAction(row, col, index)"
      >
        Click me!
      </button>
    </ng-template>
    <docs-tabs [content]="SNIPPETS"></docs-tabs>
  `,
  styles: [
    `
      .table th {
        white-space: nowrap;
      }
    `,
  ],
  encapsulation: ViewEncapsulation.None,
})
export class MobileLayoutComponent {
  @ViewChild('actions', { static: true }) actions: TemplateRef<any> | undefined;
  clicked = '';

  mobileLayout$ = new BehaviorSubject(true);
  data = [
    {
      firstName: 'Peter',
      lastName: 'Parker',
      gender: 'male',
      favoriteFood: 'Pasta',
    },
    {
      firstName: 'Mary Jane',
      lastName: 'Watson',
      gender: 'female',
      favoriteFood: 'Pizza',
    },
  ];
  config$: Observable<TableConfig> = this.mobileLayout$.pipe(
    map((mobileLayout) => ({
      mobileLayout,
      columns: {
        firstName: {
          mobileHeader: true,
          sortable: true,
        },
        lastName: {
          mobileHeader: true,
          sortable: true,
        },
        gender: {
          mobileHeader: true,
          transform: {
            pipe: GenderPipe,
          },
        },
        favoriteFood: {
          mobileHeader: true,
        },
        action: {
          mobileHeader: false,
          header: false,
          templateRef: this.actions,
        },
      },
    }))
  );

  SNIPPETS = MOBILE_LAYOUT_SNIPPETS;

  toggleLayout = (): void => {
    this.mobileLayout$.next(!this.mobileLayout$.getValue());
  };
  clickAction(
    row: TableRow,
    column: { key: string; value: TableColumn },
    index: number
  ): void {
    console.log('clicked row:', row, 'col:', column);
    this.clicked = `Clicked row number: ${index}`;
  }
}

export const Mobile: Story<MobileLayoutComponent> = (
  args: MobileLayoutComponent
) => ({
  props: args,
  component: MobileLayoutComponent,
});

import { Pipe, PipeTransform } from '@angular/core';

@Pipe({
  name: 'highlight',
})
export class HighlightPipe implements PipeTransform {
  transform(text: any, searchTerm: string | null): string {
    if (!searchTerm) {
      return text;
    }
    const haystackAlwaysString = text + '';
    let highlightedText = haystackAlwaysString; // fallback

    let searchPattern;
    try {
      searchPattern = new RegExp(
        '(' +
          // @ts-ignore
          searchTerm
            .toLowerCase()
            .match(/".*?"|[^ ]+/g) // extract words
            .map(
              (needle) => needle.replace(/"(.*?)"/, '$1') // strip away '"'
            )
            .join('|') + // combine words
          ')',
        'ig'
      );
    } catch (error) {
      return highlightedText;
    }

    const containsTagPattern = /(<.*?>)(.*)(<\/.*?>)/gi;
    const containsTagMatches = containsTagPattern.exec(haystackAlwaysString);

    if (containsTagMatches) {
      // tag exists in haystack
      highlightedText =
        containsTagMatches[1] +
        containsTagMatches[2].replace(
          searchPattern,
          '<span class="gt-highlight-search">$1</span>'
        ) +
        containsTagMatches[3];
    } else {
      highlightedText = haystackAlwaysString.replace(
        searchPattern,
        '<span class="gt-highlight-search">$1</span>'
      );
    }

    return highlightedText;
  }
}

import { Pipe, PipeTransform } from '@angular/core';
import { Order } from '../enums/order.enum';

@Pipe({
  name: 'sortClass',
})
export class SortClassPipe implements PipeTransform {
  transform(
    selection: { sortBy: string; sortByOrder: Order } | any,
    property: string,
    aria = false
  ): string | null {
    return selection?.sortBy === property
      ? !aria
        ? 'gt-sort-' + selection.sortByOrder
        : selection.sortByOrder + 'ending'
      : !aria
      ? ''
      : null;
  }
}

