    | TemplateRef<GtDeltaComponent>
    | undefined;
  loading$ = new BehaviorSubject(false);
  config: TableConfig = {};
  data: TableRows = [];

  ngOnInit(): void {
    this.config = {
      stickyHeaders: {
        row: true,
      },
      mobileLayout: true,
      rows: {
        year: {
          class: 'text-end',
          header: false,
        },
        value: {
          class: 'text-end',
        },
        delta: {
          header: 'Delta %',
          templateRef: this.delta,
          class: 'text-end',
        },
        deltaIndex: {
          header: 'Since inception %',
