      },
      {
        year: '2021',
        value: 50,
        feeling: 'negative',
      },
    ];
  }

  SNIPPETS = HORIZONTAL_TABLE_SNIPPETS;
}

export const Horizontal: Story<HorizontalTableComponent> = (
  args: HorizontalTableComponent
) => ({
  props: args,
  component: HorizontalTableComponent,
});

